sys_ui_script.script:
var x_376096_dsutils = x_376096_dsutils || {};
x_376096_dsutils.form_layout = (function() {
	"use strict";

	/* global setTimeout */
	/* eslint no-undef: "error" */
	if (x_376096_dsutils.form_layout) {
		return x_376096_dsutils.form_layout;
	}

	x_376096_dsutils.loader.get('window', function ($window) {
		const editPageForm = $window.document.querySelector('form[name=editPage]');
		const uiSection = editPageForm && editPageForm.querySelector('input[name=sys_target]');
		if (editPageForm && uiSection) {
			var ga = new $window.GlideAjax('DSUtilClient').setScope('x_376096_dsutils');
			ga.addParam('sysparm_name', 'testUISection');
			ga.addParam('ui_section', uiSection.value);
			ga.getXMLAnswer(function (result) {
				result = JSON.parse(result);
				if (result.status == 'error' && result.message) {
					const alertMsg = {
						msg: result.message,
						id: 'dsutils_domain_msg',
						type: 'warning'
					};

					const ui = $window.GlideUI.get();
					ui.addOutputMessage(alertMsg);
					// Prevent "Wrong Scope" message from clearing the Domain Separation warning
					ui.clearOutputMessages = _ => {};
				}
			});
		}
	});

	return {
		type: 'form_layout'
	};
})();
